INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B1_I1', 'ingles-c1-U10', 1, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''demand'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''demand'':","mastery_tag":"grammar:c1-subjunctive","explanation":"El subjuntivo se utiliza en este contexto para expresar la necesidad o urgencia de que se complete algo.","stimulus_en":"It is essential that we finish this project on time.\n\nWe demand that we finish... ...","correct_answer":"this project on time.","example":"demand"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B1_I2', 'ingles-c1-U10', 2, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''recommend'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''recommend'':","mastery_tag":"grammar:c1-subjunctive","explanation":"Usamos ''recommend'' con subjuntivo para expresar una sugerencia en inglés formal.","stimulus_en":"He suggested we go to the meeting early.\n\nHe recommends that we go... ...","correct_answer":"to the meeting early.","example":"recommend"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B1_I3', 'ingles-c1-U10', 3, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''require'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''require'':","mastery_tag":"grammar:c1-subjunctive","explanation":"El uso de ''require'' aquí es un ejemplo de cómo el subjuntivo se utiliza para expresar insistencia.","stimulus_en":"They insist that he apologize immediately.\n\nThey require that he apologize... ...","correct_answer":"immediately.","example":"require"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B1_I4', 'ingles-c1-U10', 4, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''hope'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''hope'':","mastery_tag":"grammar:c1-subjunctive","explanation":"El subjuntivo es necesario aquí porque estamos expresando un deseo que no es real.","stimulus_en":"I wish she were here for the celebration.\n\nI hope that she were... ...","correct_answer":"here for the celebration.","example":"hope"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B1_I5', 'ingles-c1-U10', 5, 'key_word_transformation', 'Reescribe la frase usando la palabra clave ''stress'':', '{"prompt_es":"Reescribe la frase usando la palabra clave ''stress'':","mastery_tag":"grammar:c1-subjunctive","explanation":"El subjuntivo se utiliza para expresar la importancia de una acción en presentaciones formales.","stimulus_en":"It’s important that she take the lead in this project.\n\nThey stress that she take... ...","correct_answer":"the lead in this project.","example":"stress"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B2_I1', 'ingles-c1-U10', 6, 'fill_blanks', 'Fill in the blanks with the correct word to complete each sentence.', '{"prompt_es":"Fill in the blanks with the correct word to complete each sentence.","mastery_tag":"listening:c1-complex-audio","main_text":"Listening to complex audio materials often requires the ability to understand ___1___ meanings and subtle nuances. When a speaker uses irony or sarcasm, the listener must draw ___2___ based on the context rather than the explicit words. In many cases, the tone of voice can convey ___3___ information that is not directly stated. Moreover, effective comprehension involves recognizing the speaker''s intention, which may imply ___4___ undercurrents. Ultimately, a nuanced understanding allows for deeper ___5___ of the material presented.","correct_answer":["implicit","inferences","additional","emotional","appreciation"],"explanation":"(1) La palabra ''implicit'' se refiere a significados que no se expresan de forma directa, sino que se infieren del contexto.\n(2) Las ''inferences'' son conclusiones que se sacan basadas en la información implícita y el contexto.\n(3) La palabra ''additional'' se refiere a la información extra que se comunica sin ser explícitamente mencionada.\n(4) La palabra ''emotional'' se refiere a los sentimientos que pueden estar implícitos en el mensaje del hablante.\n(5) La palabra ''appreciation'' se refiere a la comprensión y valoración más rica de un material."}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B3_I1', 'ingles-c1-U10', 7, 'multiple_choice', 'Which word best describes a situation that is not straightforward and has many layers?', '{"prompt_es":"Which word best describes a situation that is not straightforward and has many layers?","mastery_tag":"vocabulary:c1-nuanced","explanation":"La palabra ''nuanced'' describe algo que tiene varias capas o sutilezas, a diferencia de ''obvious'' o ''simple''.","stimulus_en":"","options":[{"id":"o1","text":"Transparent"},{"id":"o2","text":"Nuanced"},{"id":"o3","text":"Obvious"},{"id":"o4","text":"Simple"}],"correct_answer":"o2"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C1_U10_B3_I2', 'ingles-c1-U10', 8, 'multiple_choice', 'What term refers to the emotional undertone of a word beyond its literal meaning?', '{"prompt_es":"What term refers to the emotional undertone of a word beyond its literal meaning?","mastery_tag":"vocabulary:c1-nuanced","explanation":"''Connotation'' se refiere a los significados o emociones adicionales que una palabra puede evocar más allá de su definición literal.","stimulus_en":"","options":[{"id":"o1","text":"Connotation"},{"id":"o2","text":"Denotation"},{"id":"o3","text":"Definition"},{"id":"o4","text":"Etymology"}],"correct_answer":"o1"}')
