INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U1_B1_I1', 'ingles-c2-unit1', 1, 'multiple_choice', 'Choose the correct idiom to complete the sentence about a difficult situation:', '{"prompt_es":"Choose the correct idiom to complete the sentence about a difficult situation:","stimulus_en":"The company is currently ___ between a rock and a hard place regarding the new regulations.","options":[{"id":"o1","text":"stuck"},{"id":"o2","text":"caught"},{"id":"o3","text":"placed"},{"id":"o4","text":"situated"}],"correct_answer":"o2","explanation":"To be ''caught between a rock and a hard place'' is a common idiom meaning to be in a situation where you must choose between two equally unpleasant alternatives.","concept_tags":["vocabulary","idioms","advanced"],"complexity":4}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U1_B1_I2', 'ingles-c2-unit1', 2, 'matching', 'Match the C2-level idioms with their meanings:', '{"prompt_es":"Match the C2-level idioms with their meanings:","pairs":[{"id":"p1","left":"To play second fiddle","right":"To take a subordinate role"},{"id":"p2","left":"To bark up the wrong tree","right":"To have a mistaken idea"},{"id":"p3","left":"To steal someone''s thunder","right":"To take credit for someone else''s work"}],"concept_tags":["vocabulary","idioms"],"complexity":4}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;

INSERT INTO public.course_lessons (id, module_id, order_index, title, duration)
