VALUES ('C2_U2_B3_I1', 'ingles-c2-U2', 7, 'multiple_choice', 'Which rhetorical strategy involves appealing to the audience''s emotions?', '{"prompt_es":"Which rhetorical strategy involves appealing to the audience''s emotions?","mastery_tag":"speaking:c2-rhetorical-skills","explanation":"Pathos se refiere a apelar a las emociones del público para persuadirlo.","stimulus_en":"","options":[{"id":"o1","text":"Ethos"},{"id":"o2","text":"Pathos"},{"id":"o3","text":"Logos"},{"id":"o4","text":"Kairos"}],"correct_answer":"o2"}')
