INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U2_B3_I2', 'ingles-c2-U2', 8, 'multiple_choice', 'In a debate, which technique is used to highlight the weaknesses in an opponent''s argument?', '{"prompt_es":"In a debate, which technique is used to highlight the weaknesses in an opponent''s argument?","mastery_tag":"speaking:c2-rhetorical-skills","explanation":"La refutación se utiliza para contrarrestar o debilitar el argumento del oponente.","stimulus_en":"","options":[{"id":"o1","text":"Refutation"},{"id":"o2","text":"Amplification"},{"id":"o3","text":"Exemplification"},{"id":"o4","text":"Justification"}],"correct_answer":"o1"}')
