VALUES ('C2_U2_B3_I3', 'ingles-c2-U2', 9, 'multiple_choice', 'What is the purpose of using rhetorical questions in a speech?', '{"prompt_es":"What is the purpose of using rhetorical questions in a speech?","mastery_tag":"speaking:c2-rhetorical-skills","explanation":"Las preguntas retóricas se utilizan para hacer que la audiencia reflexione sobre un tema.","stimulus_en":"","options":[{"id":"o1","text":"To confuse the audience"},{"id":"o2","text":"To provoke thought"},{"id":"o3","text":"To provide evidence"},{"id":"o4","text":"To summarize points"}],"correct_answer":"o2"}')
