INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U2_B3_I4', 'ingles-c2-U2', 10, 'multiple_choice', 'Which of the following is a key component of effective persuasion?', '{"prompt_es":"Which of the following is a key component of effective persuasion?","mastery_tag":"speaking:c2-rhetorical-skills","explanation":"La claridad es fundamental en la persuasión, ya que ayuda a que el mensaje sea comprendido.","stimulus_en":"","options":[{"id":"o1","text":"Clarity"},{"id":"o2","text":"Ambiguity"},{"id":"o3","text":"Overgeneralization"},{"id":"o4","text":"Procrastination"}],"correct_answer":"o1"}')
