VALUES ('C2_U2_B3_I5', 'ingles-c2-U2', 11, 'multiple_choice', 'What is the term for the credibility of the speaker in a rhetorical context?', '{"prompt_es":"What is the term for the credibility of the speaker in a rhetorical context?","mastery_tag":"speaking:c2-rhetorical-skills","explanation":"Ethos se refiere a la credibilidad y autoridad del orador en el discurso.","stimulus_en":"","options":[{"id":"o1","text":"Pathos"},{"id":"o2","text":"Logos"},{"id":"o3","text":"Ethos"},{"id":"o4","text":"Diction"}],"correct_answer":"o3"}')
