VALUES ('C2_U3_B1_I1', 'ingles-c2-U3', 1, 'multiple_choice', 'Which word best replaces ''show'' in a more sophisticated context?', '{"prompt_es":"Which word best replaces ''show'' in a more sophisticated context?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"Todas las opciones presentan un nivel de sofisticación más alto que ''show''.","stimulus_en":"","options":[{"id":"o1","text":"demonstrate"},{"id":"o2","text":"exhibit"},{"id":"o3","text":"indicate"},{"id":"o4","text":"all of the above"}],"correct_answer":"o4"}')
