INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U3_B1_I2', 'ingles-c2-U3', 2, 'multiple_choice', 'What is the term for a brief summary at the end of an academic paper?', '{"prompt_es":"What is the term for a brief summary at the end of an academic paper?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"La conclusión es un resumen efectivo de los puntos clave del trabajo académico.","stimulus_en":"","options":[{"id":"o1","text":"abstract"},{"id":"o2","text":"appendix"},{"id":"o3","text":"conclusion"},{"id":"o4","text":"overview"}],"correct_answer":"o3"}')
