INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U3_B1_I3', 'ingles-c2-U3', 3, 'multiple_choice', 'Which sentence structure adds sophistication to writing?', '{"prompt_es":"Which sentence structure adds sophistication to writing?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"Esta estructura es más formal y sofisticada que las demás opciones.","stimulus_en":"","options":[{"id":"o1","text":"I think that."},{"id":"o2","text":"It is important to note that."},{"id":"o3","text":"To conclude, I believe."},{"id":"o4","text":"All are simple."}],"correct_answer":"o2"}')
