VALUES ('C2_U5_B1_I1', 'ingles-c2-U5', 1, 'multiple_choice', 'Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.', '{"prompt_es":"Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.","mastery_tag":"grammar:c2-stylistic-variation","explanation":"La ironía se utiliza para aumentar el impacto del tema, destacando su seriedad de manera crítica.","stimulus_en":"The author''s use of irony in the narrative effectively ___ the seriousness of the subject matter.","options":[{"id":"o1","text":"diminishes"},{"id":"o2","text":"amplifies"},{"id":"o3","text":"neutralizes"},{"id":"o4","text":"confuses"}],"correct_answer":"o2"}')
