INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B1_I2', 'ingles-c2-U5', 2, 'multiple_choice', 'Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.', '{"prompt_es":"Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.","mastery_tag":"grammar:c2-stylistic-variation","explanation":"Una pregunta retórica invita a los lectores a pensar y conectarse más con el texto.","stimulus_en":"Employing a rhetorical question can ___ the audience''s engagement with the text.","options":[{"id":"o1","text":"distract"},{"id":"o2","text":"encourage"},{"id":"o3","text":"bore"},{"id":"o4","text":"alienate"}],"correct_answer":"o2"}')
