INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B1_I3', 'ingles-c2-U5', 3, 'multiple_choice', 'Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.', '{"prompt_es":"Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.","mastery_tag":"grammar:c2-stylistic-variation","explanation":"Un tono formal refuerza la credibilidad y seriedad del autor en un contexto académico.","stimulus_en":"The deliberate choice of a formal tone can ___ the author''s credibility in an academic paper.","options":[{"id":"o1","text":"weaken"},{"id":"o2","text":"bolster"},{"id":"o3","text":"complicate"},{"id":"o4","text":"dilute"}],"correct_answer":"o2"}')
