VALUES ('C2_U5_B1_I4', 'ingles-c2-U5', 4, 'multiple_choice', 'Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.', '{"prompt_es":"Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.","mastery_tag":"grammar:c2-stylistic-variation","explanation":"La hipérbole se utiliza para aumentar el atractivo del producto de manera exagerada.","stimulus_en":"Using hyperbole in advertising is a common technique to ___ the product''s desirability.","options":[{"id":"o1","text":"diminish"},{"id":"o2","text":"enhance"},{"id":"o3","text":"ignore"},{"id":"o4","text":"discredit"}],"correct_answer":"o2"}')
