INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B1_I5', 'ingles-c2-U5', 5, 'multiple_choice', 'Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.', '{"prompt_es":"Choose the best option that completes each sentence to demonstrate understanding of stylistic variation.","mastery_tag":"grammar:c2-stylistic-variation","explanation":"Los eufemismos suavizan el lenguaje y ayudan a disminuir la incomodidad en conversaciones difíciles.","stimulus_en":"The use of euphemisms in sensitive topics can help to ___ discomfort among the audience.","options":[{"id":"o1","text":"create"},{"id":"o2","text":"reduce"},{"id":"o3","text":"intensify"},{"id":"o4","text":"highlight"}],"correct_answer":"o2"}')
