INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B2_I1', 'ingles-c2-U5', 6, 'multiple_choice', 'Which word best conveys a nuanced meaning in an academic context?', '{"prompt_es":"Which word best conveys a nuanced meaning in an academic context?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"La palabra ''meticuloso'' indica atención al detalle y precisión, lo cual es vital en la escritura académica.","stimulus_en":"","options":[{"id":"o1","text":"Ambiguous"},{"id":"o2","text":"Obscure"},{"id":"o3","text":"Meticulous"},{"id":"o4","text":"Vague"}],"correct_answer":"o3"}')
