INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B2_I2', 'ingles-c2-U5', 7, 'multiple_choice', 'What is the primary purpose of employing sophisticated vocabulary in academic writing?', '{"prompt_es":"What is the primary purpose of employing sophisticated vocabulary in academic writing?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"El uso de un vocabulario sofisticado debe mejorar la claridad y la precisión de la escritura, no solo impresionar.","stimulus_en":"","options":[{"id":"o1","text":"To confuse the reader"},{"id":"o2","text":"To enhance clarity"},{"id":"o3","text":"To showcase knowledge"},{"id":"o4","text":"To impress others"}],"correct_answer":"o2"}')
