INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B2_I3', 'ingles-c2-U5', 8, 'multiple_choice', 'In sophisticated writing, which technique is most effective for presenting complex ideas?', '{"prompt_es":"In sophisticated writing, which technique is most effective for presenting complex ideas?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"La paradoja puede hacer que conceptos complejos sean más accesibles al lector al reflejar la complejidad de la realidad.","stimulus_en":"","options":[{"id":"o1","text":"Hyperbole"},{"id":"o2","text":"Paradox"},{"id":"o3","text":"Simplicity"},{"id":"o4","text":"Repetition"}],"correct_answer":"o2"}')
