INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B2_I4', 'ingles-c2-U5', 9, 'multiple_choice', 'What role does context play in sophisticated writing?', '{"prompt_es":"What role does context play in sophisticated writing?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"El contexto es fundamental en la escritura sofisticada ya que ayuda a moldar y enriquecer el significado del texto.","stimulus_en":"","options":[{"id":"o1","text":"It is irrelevant"},{"id":"o2","text":"It requires extensive elaboration"},{"id":"o3","text":"It shapes meaning"},{"id":"o4","text":"It distracts from the argument"}],"correct_answer":"o3"}')
