INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B2_I5', 'ingles-c2-U5', 10, 'multiple_choice', 'Why is varying sentence structure important in advanced writing?', '{"prompt_es":"Why is varying sentence structure important in advanced writing?","mastery_tag":"writing:c2-sophisticated-writing","explanation":"Variar la estructura de las oraciones ayuda a mantener el interés del lector y hace que el texto sea más dinámico.","stimulus_en":"","options":[{"id":"o1","text":"To reduce word count"},{"id":"o2","text":"To maintain reader interest"},{"id":"o3","text":"To simplify content"},{"id":"o4","text":"To adhere to rules"}],"correct_answer":"o2"}')
