INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B3_I1', 'ingles-c2-U5', 11, 'multiple_choice', 'Which of the following sentences uses the correct subjunctive mood?', '{"prompt_es":"Which of the following sentences uses the correct subjunctive mood?","mastery_tag":"grammar:c2-mastery","explanation":"En inglés, la forma correcta del subjuntivo es ''were'' en lugar de ''was'' para situaciones hipotéticas.","stimulus_en":"","options":[{"id":"o1","text":"If I was rich, I would travel the world."},{"id":"o2","text":"If I were rich, I would travel the world."},{"id":"o3","text":"If I am rich, I would travel the world."},{"id":"o4","text":"If I have been rich, I would travel the world."}],"correct_answer":"o2"}')
