INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B3_I2', 'ingles-c2-U5', 12, 'multiple_choice', 'Choose the correct answer from the options provided.', '{"prompt_es":"Choose the correct answer from the options provided.","mastery_tag":"grammar:c2-mastery","explanation":"El verbo ''enjoy'' siempre se usa con un gerundio (la forma -ing) para expresar disfrute.","stimulus_en":"Identify the sentence with the correct use of a gerund.","options":[{"id":"o1","text":"She enjoys to swim in the ocean."},{"id":"o2","text":"She enjoys swimming in the ocean."},{"id":"o3","text":"She enjoy swimming in the ocean."},{"id":"o4","text":"She is enjoy swimming in the ocean."}],"correct_answer":"o2"}')
