INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B3_I3', 'ingles-c2-U5', 13, 'multiple_choice', 'Which sentence correctly employs an inversion for emphasis?', '{"prompt_es":"Which sentence correctly employs an inversion for emphasis?","mastery_tag":"grammar:c2-mastery","explanation":"La inversión se utiliza para dar énfasis en una oración negativa. ''Never'' al principio requiere la inversión del sujeto y el verbo.","stimulus_en":"","options":[{"id":"o1","text":"Never have I seen such a beautiful sunset."},{"id":"o2","text":"I have never seen such a beautiful sunset."},{"id":"o3","text":"I have seen never such a beautiful sunset."},{"id":"o4","text":"Such a beautiful sunset have I seen never."}],"correct_answer":"o1"}')
ON CONFLICT (id) DO UPDATE SET content = EXCLUDED.content, type = EXCLUDED.type;
INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B3_I4', 'ingles-c2-U5', 14, 'multiple_choice', 'Choose the correct answer from the options provided.', '{"prompt_es":"Choose the correct answer from the options provided.","mastery_tag":"grammar:c2-mastery","explanation":"El condicional perfecto se usa para referirse a situaciones pasadas que no ocurrieron. Se forma con ''had'' + past participle.","stimulus_en":"Select the sentence that best demonstrates the use of a conditional perfect.","options":[{"id":"o1","text":"If she had studied harder, she would have passed the exam."},{"id":"o2","text":"If she studies harder, she will pass the exam."},{"id":"o3","text":"If she was studying harder, she would pass the exam."},{"id":"o4","text":"If she would have studied harder, she passed the exam."}],"correct_answer":"o1"}')
