INSERT INTO public.course_exercises (id, lesson_id, order_index, type, title, content)
VALUES ('C2_U5_B3_I5', 'ingles-c2-U5', 15, 'multiple_choice', 'Which of the following sentences shows correct parallel structure?', '{"prompt_es":"Which of the following sentences shows correct parallel structure?","mastery_tag":"grammar:c2-mastery","explanation":"La estructura paralela significa que los elementos de una lista deben estar en la misma forma gramatical. ''Reading'', ''swimming'', y ''jogging'' son todos gerundios.","stimulus_en":"","options":[{"id":"o1","text":"She likes reading, to swim, and jogging."},{"id":"o2","text":"She likes to read, swimming, and to jog."},{"id":"o3","text":"She likes reading, swimming, and jogging."},{"id":"o4","text":"She likes to read, to swim, and jog."}],"correct_answer":"o3"}')
